+      transition: all 0.3s ease;
+      margin-bottom: 1.5rem;
+    }
+    .download-btn:hover {
+      background: linear-gradient(90deg, #0056d1, #00a2e8);
+      transform: scale(1.05);
+    }
+
+    /* ===== QR Section ===== */
+    .qr-section {
+      display: flex;
+      flex-direction: column;
+      align-items: center;
+      margin-top: 1rem;
+    }
+    .qr-section img {
+      width: 150px;
+      height: 150px;
+      margin-bottom: 0.5rem;
+      border: 3px solid #e6f4fe;
+      border-radius: 12px;
+      transition: 0.3s ease;
+    }
