+    /* ===== Reset & Base ===== */
+    * { margin: 0; padding: 0; box-sizing: border-box; }
+    body {
+      font-family: "Poppins", sans-serif;
+      background: linear-gradient(135deg, #e6f4fe, #ffffff);
+      color: #111;
+      display: flex;
+      flex-direction: column;
+      align-items: center;
+      justify-content: center;
+      min-height: 100vh;
+      text-align: center;
+      padding: 1.5rem;
+    }
+
+    /* ===== Card ===== */
+    .card {
+      background: #fff;
+      border-radius: 24px;
+      box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
+      padding: 2rem;
+      max-width: 440px;
+      width: 100%;
+      transition: 0.3s ease;
+    }
+    .card:hover {
+      transform: translateY(-4px);
+      box-shadow: 0 10px 28px rgba(0, 0, 0, 0.15);
+    }
+
+    /* ===== App Icon ===== */
+    .app-logo {
+      width: 120px;
+      height: 120px;
+      border-radius: 30%;
+      margin-bottom: 1rem;
+      object-fit: cover;
+      border: 4px solid #e6f4fe;
+    }
+
+    /* ===== Text ===== */
+    h1 {
+      font-size: 1.8rem;
+      font-weight: 700;
+      margin-bottom: 0.5rem;
+    }
+    p {
+      color: #555;
+      margin-bottom: 1.8rem;
+      line-height: 1.5;
+    }
+
+    /* ===== Button ===== */
+    .download-btn {
+      display: inline-block;
+      background: linear-gradient(90deg, #007bff, #00c6ff);
+      color: white;
+      padding: 0.9rem 2.2rem;
+      border-radius: 50px;
+      font-size: 1rem;
+      font-weight: 600;
+      text-decoration: none;
+      box-shadow: 0 4px 12px rgba(0, 123, 255, 0.3);
+      transition: all 0.3s ease;
+      margin-bottom: 1.5rem;
+    }
+    .download-btn:hover {
+      background: linear-gradient(90deg, #0056d1, #00a2e8);
+      transform: scale(1.05);
+    }
+
+    /* ===== QR Section ===== */
+    .qr-section {
+      display: flex;
+      flex-direction: column;
+      align-items: center;
+      margin-top: 1rem;
+    }
+    .qr-section img {
+      width: 150px;
+      height: 150px;
+      margin-bottom: 0.5rem;
+      border: 3px solid #e6f4fe;
+      border-radius: 12px;
+      transition: 0.3s ease;
+    }
+    .qr-section img:hover {
+      transform: scale(1.05);
+    }
+    .qr-section span {
+      color: #666;
+      font-size: 0.9rem;
+    }
+
+    /* ===== Footer ===== */
+    footer {
+      margin-top: 2rem;
+      font-size: 0.85rem;
+      color: #777;
+    }
+
+    /* ===== Responsive ===== */
+    @media (max-width: 480px) {
+      .card {
+        padding: 1.5rem;
+      }
+      h1 {
+        font-size: 1.5rem;
+      }
+    }
+  </style>
+</head>
+<body>
+  <div class="card">
+    <img src="./assets/images/cric2.png" alt="Cric Craze Logo" class="app-logo" />
+    <h1>Cric Craze</h1>
+    <p>The ultimate cricket experience ‚Äî live scores, streams, and tournaments. Download now and join the craze!</p>
+
+    <!-- üîó Replace with your actual APK URL -->
